INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 15, 7, N'mamaivamso jiva-loke    jiva-bhutah sanatanah    manah-sasthanindriyani    prakrti-sthani karsati'
,
N'mama--My; eva--certainly; amsah--fragmental particles; jiva-loke--worldof conditional life; jiva-bhutah--the conditioned living entity;sanatanah--eternal; manah--mind; sasthani--six; indriyani--senses;prakrti--material nature; sthani--situated; karsati--struggling hard.'
,
