INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 15, 8, N'sariram yad avapnoti    yac capy utkramatisvarah    grhitvaitani samyati    vayur gandhan ivasayat'
