INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 15, 10, N'utkramantam sthitam vapi    bhunjanam va gunanvitam    vimudha nanupasyanti    pasyanti jnana-caksusah'
,
N'utkramantam--quitting the body; sthitam--situated in the body; va api--either; bhunjanam--enjoying; va--or; guna-anvitam--under the spell ofthe modes of material nature; vimudhah--foolish persons; na--never;anupasyanti--can see; pasyanti--one can see; jnana-caksusah--one whohas the eyes of knowledge.'
,
