INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 15, 11, N'yatanto yoginas cainam    pasyanty atmany avasthitam    yatanto  py akrtatmano    nainam pasyanty acetasah'
,
N'yatantah--endeavoring; yoginah--transcendentalists; ca--also; enam--this; pasyanti--can see; atmani--in the self; avasthitam--situated;yatantah--although endeavoring; api--although; akrta-atmanah--withoutself-realization; na--does not; enam--this; pasyanti--can see;acetasah--undeveloped mind.'
