INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 15, 12, N'yad aditya-gatam tejo    jagad bhasayate  khilam    yac candramasi yac cagnau    tat tejo viddhi mamakam'
