INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  6, N'ajo  pi sann avyayatma    bhutanam isvaro  pi san    prakrtim svam adhisthaya    sambhavamy atma-mayaya'
, N'ajah--unborn; api--although; san--being so; avyaya--without deterioration; atma--body; bhutanam--all those who are born; isvarah-- the Supreme Lord; api--although; san--being so; prakrtim--transcendental form; svam--of Myself; adhisthaya--being so situated; sambhavami--I do incarnate; atma-mayaya--by My internal energy.'
