INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  9, N'janma karma ca me divyam    evam yo vetti tattvatah    tyaktva deham punar janma    naiti mam eti so  rjuna'
