INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  10, N'vita-raga-bhaya-krodha    man-maya mam upasritah    bahavo jnana-tapasa    puta mad-bhavam agatah'
, N'vita--freed from; raga--attachment; bhaya--fear; krodhah--anger; matmaya-- fully in Me; mam--unto Me; upasritah--being fully situated; bahavah--many; jnana--knowledge; tapasa--by penance; putah--beingpurified; mat-bhavam--transcendental love for Me; agatah--attained. '
, N'Being freed from attachment, fear and anger, being fully absorbed in Me and taking refuge in Me, many, many persons in the past became purified by knowledge of Me--and thus they all attained transcendental love for Me.');

INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
