INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  12, N'kanksantah karmanam siddhim    yajanta iha devatah    ksipram hi manuse loke    siddhir bhavati karma-ja'
