INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  13, N'catur-varnyam maya srstam    guna-karma-vibhagasah    tasya kartaram api mam    viddhy akartaram avyayam'
, N'catuh-varnyam--the four divisions of human society; maya--by Me; srstam--created; guna--quality; karma--work; vibhagasah--in terms of division; tasya--of that; kartaram--the father; api--although; mam--Me;viddhi--you may know; akartaram--as the non-doer; avyayam--being unchangeable.'
