INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  14, N'na mam karmani limpanti    na me karma-phale sprha    iti mam yo  bhijanati    karmabhir na sa badhyate'
, N'na--never; mam--unto Me; karmani--all kinds of work; limpanti--do affect; na--nor; me--My; karma-phale--in fruitive action; sprha-- aspiration; iti--thus; mam--unto Me; yah--one who; abhijanati--doesknow; karmabhih--by the reaction of such work; na--never does; sah--he; badhyate--becomes entangled.'
