INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  15, N'evam jnatva krtam karma    purvair api mumuksubhih    kuru karmaiva tasmat tvam    purvaih purvataram krtam'
