INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  19, N'yasya sarve samarambhah    kama-sankalpa-varjitah    jnanagni-dagdha-karmanam    tam ahuh panditam budhah'
