INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  21, N'nirasir yata-cittatma    tyakta-sarva-parigrahah    sariram kevalam karma    kurvan napnoti kilbisam'
