INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 4,  24, N'brahmarpanam brahma havir    brahmagnau brahmana hutam    brahmaiva tena gantavyam    brahma-karma-samadhina'
