INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  4, N'yada hi nendriyarthesu    na karmasv anusajjate    sarva-sankalpa-sannyasi    yogarudhas tadocyate'
