INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  7, N'jitatmanah prasantasya    paramatma samahitah    sitosna-sukha-duhkhesu    tatha manapamanayoh'
