INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  8, N'jnana-vijnana-trptatma    kuta-stho vijitendriyah    yukta ity ucyate yogi    sama-lostrasma-kancanah'
, N'jnana--acquired knowledge; vijnana--realized knowledge; trpta-- satisfied; atma--living entity; kuta-sthah--spiritually situated; vijita-indriyah--sensually controlled; yuktah--competent for selfrealization;iti--thus; ucyate--is said; yogi--the mystic; sama-- equipoised; lostra--pebbles; asma--stone; kancanah--gold.'
