INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  9, N'suhrn-mitrary-udasinamadhyastha-    dvesya-bandhusu    sadhusv api ca papesu    sama-buddhir visisyate'
