INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  10, N'yogi yunjita satatam    atmanam rahasi sthitah    ekaki yata-cittatma    nirasir aparigrahah'
