INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  15, N'yunjann evam sadatmanam    yogi niyata-manasah    santim nirvana-paramam    mat-samstham adhigacchati'
