INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  24, N'sa niscayena yoktavyo    yogo  nirvinna-cetasa    sankalpa-prabhavan kamams    tyaktva sarvan asesatah    manasaivendriya-gramam    viniyamya samantatah'
