INSERT [dbo].[Cards] ([Id], [Chapter], [Verse], [Description], [Synonmys], [Meaning]) 
VALUES (NEWID(), 6,  27, N'prasanta-manasam hy enam    yoginam sukham uttamam    upaiti santa-rajasam    brahma-bhutam akalmasam'
